const person={
    name:'elliot',
    surname:'alderson',
    age:'29',
    phrase:'hello friend',
}
//desestructuracion

const useContext=( {name,surname,phrase,clave='123',age})=>{
    //extrae la propiedad en {} de person
    //console.log(name,surname,phrase)
    return{
        nombreclave:clave,
        age:age,
        latlon:{
            lat:2121,
            lon:8982,
        },
    }
};
const {nombreclave,age,latlon:{lat,lon}}=useContext(person);
console.log(nombreclave,age,lat,lon);
