const person={
    name:'elliot',
    surname:'alderson',
    age:'29',
    phrase:'hello friend',
}
//desestructuracion

const useContext=( {name,surname,phrase,clave='123',age})=>{
    //extrae la propiedad en {} de person
    //console.log(name,surname,phrase)
    return{
        nombreclave:clave,
        age:age,
    }
};
useContext(person);
