import {getHeroesById} from './bases/export'
const promise = new Promise((resolve,reject)=>{

    setTimeout(()=>{
        const hero=getHeroesById(15)
        resolve(hero)
        reject('no se pudo encontrar al heroe')
    },200)
});

promise.then((heroe)=>{
    console.log(heroe)
})
.catch(err=>console.log(err))
