import {getHeroesById} from './bases/export'
const getHeroByIdAsync=(id)=>{
    return new Promise((resolve,reject)=>{
        setTimeout(()=>{
            const hero=getHeroesById(id)
            resolve(hero)
            //reject('no se pudo encontrar al heroe')
        },200)
    });
}
getHeroByIdAsync(1).then(e=>console.log(e))
